      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        user: {
          usernameLogin,
          passwordLogin
        }
      })
    });
  const {data} = await response.json();
    const token = data?.token;
    if (token) {
    setToken(token);
