      method: "GET",
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
    })
    const {data} = await response.json();
    console.log("data:", data)
    setUser(data)
    setUsername('');
    setPassword('');
    history.push('/');
  }
}



